import React, {useState, useEffect} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import './App.css';
import { v4 as uuid_v4 } from 'uuid';
import  api from "../api/contacts";  
import Header from "./Header";
import AddContact from "./AddContact";
import EditContact from "./EditContact";
import ContactList from "./ContactList";
import ContactDetails from "./ContactDetails"

function App() {
  const LOCAL_STORAGE_KEY = 'contacts'
  const [contacts , setContacts] = useState([]); //hook

  //retrieve contacts 
  const retrieveContacts = async () =>{
    const response = await api.get("/contacts"); //apppend /contact to base url
    return response.data;

    }

  const addContactHandler = async(contact)=>{
    console.log(contact)
    const request = {
      id:uuid_v4(),
      ...contact

    }


    const response = await api.post("/contacts", request);
    setContacts([...contacts,response.data]);  ///set contacts from addContacts 

  }

  const updateContactHandler = async(contact) =>{
    const response = await api.put(`/contacts/${contact.id}`,contact);
    const {id,name,email} = response.data;
    setContacts(

    )

  }
  const removeContactHandler = async (id) => {
    await api.delete(`/contacts/${id}`);
    const newContactList = contacts.filter((contact) => {
      return contact.id !== id;
    });

    setContacts(newContactList);
  }

  useEffect(() =>{  
    // const retrieveContacts = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    // if(retrieveContacts){
    //   setContacts(retrieveContacts);
    // }
    const getAllContacts = async ()=>{
      const allContacts = await retrieveContacts();
      if(allContacts) setContacts(allContacts);

    };
    getAllContacts();
  },
  [])

  useEffect(() =>{
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(contacts))
  },[contacts])

  return (
    <div className="ui container">
      <Router>
        <Header />
        <Switch>
          <Route
           path="/"
           exact
           render = {(props) =>(
            <ContactList 
              {...props}
              contacts={contacts}
               getContactId={removeContactHandler}
            />
             )}
            />
          <Route
           path="/add"
           render = {(props) =>(
            <AddContact 
              {...props}
              getNewContact={addContactHandler} 
            />
             )}
          />
           <Route
           path="/edit"
           render = {(props) =>(
            <EditContact 
              {...props}
              updateContactHandler={updateContactHandler} 
            />
             )}
          />
          <Route path="/contact/:id" component={ContactDetails}/> 

        </Switch>
      </Router>


    </div>
  );
}

export default App;
